mapper.select:
SELECT o.order_number, MAX(o.order_date) AS order_date,
(SELECT p.product_name FROM product p INNER JOIN order_detail od ON p.product_number = od.product_number WHERE od.order_number = o.order_number ORDER BY p.product_name FETCH FIRST 1 ROW ONLY) AS first_product_name, (SELECT COUNT(*) FROM order_detail WHERE order_number = o.order_number) - 1 AS additional_product_count,
SUM(od.order_quantity) AS total_quantity, MAX(o.shipping_fee) AS shipping_fee, MAX(o.order_status) AS order_status, MAX(u.user_name) AS orderer_name, SUM(od.price * od.order_quantity) AS total_price, MAX(pm.payment_method) AS payment_method FROM orders o INNER JOIN order_detail od ON o.order_number = od.order_number INNER JOIN product p ON od.product_number = p.product_number INNER JOIN payment pm ON o.order_number = pm.order_number INNER JOIN users u ON o.user_id = u.user_id WHERE 1 = 1 AND o.order_status = 1
AND o.order_number = #{searchKeyword}
AND p.product_name LIKE '%' || #{searchKeyword} || '%'
AND u.user_name LIKE '%' || #{searchKeyword} || '%'
AND o.order_date BETWEEN #{orderDateStart} AND #{orderDateEnd}
AND pm.payment_method = #{paymentMethod}
GROUP BY o.order_number ORDER BY o.order_number

mapper.update:
UPDATE orders
SET order_status = 2
WHERE order_number = #{orderNumber}

UPDATE orders
SET tracking_number = #{trackingNumber}
WHERE order_number = #{orderNumber}

UPDATE orders
SET order_status = 3
WHERE order_number = #{orderNumber}

UPDATE orders
SET order_status = 4
WHERE order_number = #{orderNumber}

mapper.select:
SELECT o.order_number, MAX(o.order_date) AS order_date,
(SELECT p.product_name FROM product p INNER JOIN order_detail od ON p.product_number = od.product_number WHERE od.order_number = o.order_number ORDER BY p.product_name FETCH FIRST 1 ROW ONLY) AS first_product_name, (SELECT COUNT(*) FROM order_detail WHERE order_number = o.order_number) - 1 AS additional_product_count,
SUM(od.order_quantity) AS total_quantity, MAX(o.shipping_fee) AS shipping_fee, MAX(o.order_status) AS order_status, MAX(u.user_name) AS orderer_name, SUM(od.price * od.order_quantity) AS total_price, MAX(pm.payment_method) AS payment_method FROM orders o INNER JOIN order_detail od ON o.order_number = od.order_number INNER JOIN product p ON od.product_number = p.product_number INNER JOIN payment pm ON o.order_number = pm.order_number INNER JOIN users u ON o.user_id = u.user_id WHERE 1 = 1
AND o.order_number = #{searchKeyword}
AND p.product_name LIKE '%' || #{searchKeyword} || '%'
AND u.user_name LIKE '%' || #{searchKeyword} || '%'
AND o.order_date BETWEEN #{orderDateStart} AND #{orderDateEnd}
AND pm.payment_method = #{paymentMethod}
AND o.order_status = #{orderStatus}
GROUP BY o.order_number ORDER BY o.order_number
